import React, { useEffect } from "react";
import { NavLink } from "react-router-dom";
import axios from "axios";
import { useState } from "react";
function Home() {
  const [words, setwords] = useState([]);
  const [question, setquestion] = useState({});
  const wordFetch = async () => {
    try {
      const response = await axios.get("http://localhost:3001/words");
      setwords(response.data);
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const randomQuestion = () => {
    const random = Math.floor(Math.random() * (words.length - 1 + 1));
    const random2 = Math.floor(Math.random() * (4 - 1 + 1)) + 1;
    setquestion(words[random]);
  };

  useEffect(() => {
    wordFetch();
    randomQuestion();
  }, []);

  return (
    <div className=" flex flex-col items-center w-full">
      <div className=" w-full  w-[80vw] pl-32 mt-11 h-auto">
        <div>
          <button
            to={"/"}
            className=" bg-green-700 w-[7vw] h-[3vw]  rounded-md"
          >
            Anasayfa
          </button>
          <button
            to={"/addwords"}
            className=" bg-green-700 w-[7vw] h-[3vw] ml-3  rounded-md"
          >
            Kelime Ekle
          </button>
          <button
            to={"/words"}
            className=" bg-green-700 w-[7vw] h-[3vw] ml-3 rounded-md"
          >
            Kelimeler
          </button>
        </div>
        <div className=" w-full h-auto mt-14 ml-10 ">
          <div className=" gap-8 flex flex-col">
            <p className=" text-4xl">{question.en}</p>
            <p className=" text-2xl">Türkçesi nedir ?</p>
          </div>
          <div className=" mt-10 flex justify-between pr-[15vw] text-lg">
            <div>
              <div className=" flex gap-2">
                <input type="radio" name="" id="" /> <p>Kelime</p>
              </div>
              <div className=" flex gap-2">
                <input type="radio" name="" id="" /> <p>Kelime</p>
              </div>
              <div className=" flex gap-2">
                <input type="radio" name="" id="" /> <p>Kelime</p>
              </div>
              <div className=" flex gap-2">
                <input type="radio" name="" id="" /> <p>Kelime</p>
              </div>
            </div>
            <div className=" flex gap-4">
              <div className=" bg-green-600 w-[7vw] h-[3vw] flex items-center justify-center">
                Doğru
              </div>
              <div className=" bg-red-800 w-[7vw] h-[3vw] flex items-center justify-center">
                Yanlış
              </div>
              <button
                onClick={() => {
                  randomQuestion();
                }}
                className=" bg-amber-400 w-[10vw] h-[3vw] flex items-center justify-center"
              >
                Soruyu Değiştir
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
